/* **********************************************
     Begin customfile.js
********************************************** */

(function($){
	$.fn.customFile = function(){
		this.each(function(){
			var that = $(this);
			that.addClass("customFile");
			var wraper = $("<span class='fileWraper'><input type='text' class='input-small'/><button class='button'>浏览</button></span>");
			that.after(wraper);
			wraper.prepend(that);
			wraper.on("change",function(){
				$("[type=text]",this).val($("[type=file]",this).val());
			});

		});
		return this;
	}

	$(function(){
		if(!$.browser.msie){
			$("input[type=file]").customFile();
		}
	});
})(jQuery);

/* **********************************************
     Begin imgUploadManager.js
********************************************** */

(function($){
	$.fn.imgUploadManager = function(){
		this.each(function(){
			var that = $(this);
			var file = that.find(">:file,.customFile");
			var ul = that.find(">ul");
			var tmp = "<li><img/><input type='checkbox'/><a>删除</a></li>";
			if(!file.length){
				file = $("<input type='file' name='files' />");
				that.prepend(file);
				file.customFile();
			}

			if(!ul.length){
				ul = $("<ul/>");
				that.append(ul);
			}

			that.on("change",".checkAll",$.proxy(function(e){
				var tango = $(e.target);
				$(":checkbox",this).attr("checked",!!tango.attr("checked"));
			},this));

			that.on("click",".delAll",$.proxy(function(e){
				$("li",this).has(":checked").find("a").click();
				return false;
			},this));

			that.on("change",":file",$.proxy(function(e){
				var that = $(this);
				var tango = $(e.target).select().blur();
				if(document.selection){
					var imgSrc = document.selection.createRange().text;
				}
				var preview = $(tmp);
				var img = preview.find("img");
				var FileReader = window.FileReader;

				tango.after(tango.clone(true).val(""));
				preview.append(tango.hide());
				ul.prepend(preview);

				if(FileReader){
                    var reader = new FileReader();
                    reader.onload = function(e){
                        img.attr("src",e.target.result);
                    }
                    reader.readAsDataURL(e.target.files[0]);
                }
                else{
                    img.replaceWith("<span class='img'><span></span></span>");
                    preview.find(".img span")[0].filters.item("DXImageTransform.Microsoft.AlphaImageLoader").src = imgSrc; 
                }


			},this));

			that.on("click","li",function(e){
				if($(e.target).is("a")){
					var that = $(this);
					$(this).animate({"margin":-that.innerWidth()/2,"opacity":0},"fast",function(){$(this).remove()});
				}
			});
		});
		return this;
	}

	$(function(){
		$(".imgUploadManager").imgUploadManager();
	});
})(jQuery);

/* **********************************************
     Begin dataGird.js
********************************************** */

(function($) {

	var methods = {
		ajaxRequest: function(e) {
			var that = $(this);
			if(that.is(":contains('删除')") && !confirm("确定删除吗？")){
				return false;
			}
			var postArgu = getArgu = "";
			if (e.type == "submit") {
				postArgu = that.serializeArray();
			} else {
				getArgu = $(e.target).attr("href").replace(location.protocol+"//"+location.host,"");
			}
			$[that.attr("method") || "get"](that.attr("action") + getArgu, postArgu, $.proxy(this, "render"), "json").fail($.proxy(this,"ajaxFail"));
			return false;
		},
		render: function(data) {
			if (data.error){
				alert(data.error);
			}
			else{
				$(this).html(this.tpl.render(data));
				$("tbody tr:odd", this).addClass("odd");
				$("tbody tr:even", this).addClass("even");
			}
		},
		ajaxFail:function(){
			$.error("ajaxJSONFail");
		}
	}

	$.fn.dataGrid = function() {
		this.each(function() {
			var that = $(this);
			var tpl = that.find("script[type$=juicer-tpl]").remove().html();
			var data = $.parseJSON(that.find("script[type$=json]").remove().html());
			$.extend(this, methods);
			this.tpl = juicer(tpl);
			that.on("submit", this.ajaxRequest);
			that.on("click", "a[href]:not([target])", $.proxy(this, "ajaxRequest"));
			if(data){
				this.render(data);
			}
		});
		return this;
	};

	$(function() {
		$(".dataGrid").dataGrid();
	});
})(jQuery);

/* **********************************************
     Begin observer.js
********************************************** */

(function($) {
	$(function() {
		var root = $("body");
		//当有change事件发生时，通知观察者触发dataChange事件。
		root.on("change.observer submit.observer", "[data-observer]", function(e) {
			var that = $(this);
			if(that.is("form") && e.type=="change"){
				return true;
			}
			if (e.type == "submit") {
				e.preventDefault();
			}
			var observer = $(that.data("observer"));
			var param = that.serializeArray();
			if (!param.length) {
				param = that.find(":input").serializeArray();
			}
			observer.trigger("dataChange", [param]);
		});

		//为所有有data-source属性的元素在dataChange时发起ajax请求
		root.on("dataChange.observer", "[data-source]", function(e, data) {
			var that = $(this);
			$.post(that.data("source"), data, null, "json").done(function(data) {
				that.trigger("dataRender", data);
			});
		});

		//为所有data-template-name属性的元素绑定模板渲染事件
		root.on("dataRender.observer", "[data-template-name]", function(e, data) {
			var that = $(this);
			if (!data) {
				return false;
			}
			var tpl = $("script[name=" + that.data("template-name") + "]").html();
			if (tpl) {
				that.html(juicer(tpl, data));
				setTimeout(function(){that.trigger("change");},0);
			}
		});
	});
})(jQuery);